public void GetItemExample(IWin32Window parent)
{
   try
   {
      TwainSession session = new TwainSession();
      session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

      TwainExtraImageInformation twainExtImageInfo = new TwainExtraImageInformation(1);

      twainExtImageInfo.GetInformation(0).InfoId = TwainExtendedImage.BarcodeText;
      twainExtImageInfo.GetInformation(0).ItemType = TwainItemType.Uint32;

      session.UpdateExtraImageInformation(twainExtImageInfo);

      if (twainExtImageInfo.GetInformation(0).ReturnCode == TwainReturnCode.Success)
      {
         for (int i=0; i < twainExtImageInfo.GetInformation(0).Count; i++)
         {
            uint item = (uint)twainExtImageInfo.GetInformation(0).GetItem(i, 0);
            //Do more processing on the item value
         }
      }

      session.Shutdown();
   }
   catch (Exception ex)
   {
      MessageBox.Show(ex.Message);
   }
}

public void TwainArrayCapabilityExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

   TwainCapability twCap = new TwainCapability();
   twCap.Information.ContainerType = TwainContainerType.Array;
   twCap.Information.Type = TwainCapabilityType.ImageFilter;

   twCap.ArrayCapability.ItemType = TwainItemType.Uint16;
   twCap.ArrayCapability.Count = 2;

   twCap.ArrayCapability.SetValue(0, TwainCapabilityValue.FilterRed);

   twCap.ArrayCapability.SetValue(1, TwainCapabilityValue.FilterGreen);

   session.SetCapability(twCap, TwainSetCapabilityMode.Set);
   session.Shutdown();
}

public void TwainArrayCapabilityGetValueExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

   TwainCapability twCap = session.GetCapability(TwainCapabilityType.ImageFilter, TwainGetCapabilityMode.GetValues);

   if (twCap.Information.ContainerType == TwainContainerType.Array)
   {
      TwainArrayCapability arrayCap = twCap.ArrayCapability;

      for (int i = 0; i < arrayCap.Count; i++)
      {
         TwainCapabilityValue capVal = (TwainCapabilityValue)arrayCap.GetValue(i);
         string msg = string.Format("Capability Value(%d) = %s", i, capVal.ToString());
         MessageBox.Show(msg);
      }
   }
   else
      MessageBox.Show("Container type is not array");

   session.Shutdown();
}

