public void GetJpegCompressionExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

   string jpegdata;
   try
   {
      TwainJpegCompression twainJpeg = session.GetJpegCompression(false);
      int[] quantMap = twainJpeg.GetQuantizationMap();
      int[] haffmanMap =  twainJpeg.GetHuffmanMap();
      TwainMemoryData[] quantTable = twainJpeg.GetQuantizationTable();
      TwainMemoryData[] haffmanDC = twainJpeg.GetHuffmanDC();
      TwainMemoryData[] haffmanAC = twainJpeg.GetHuffmanAC();

      jpegdata = String.Format("Color Space = {0}\nQuantMap = {1}\nGetHuffmanMap = {2}\nGetQuantTable Flags = {3}\nGetQuantTable Length = {4}\nGetQuantTable Data = {5}\nGetHuffmanDC Flags = {6}\nGetHuffmanDC Data = {7}\nGetHuffmanAC Flags = {8}\nGetHuffmanAC Data = {9}\n",
                               twainJpeg.ColorScheme,
                               quantMap[0],
                               haffmanMap[0],
                               quantTable[0].Flags,
                               quantTable[0].Length,
                               quantTable[0].Data,
                               haffmanDC[0].Flags,
                               haffmanDC[0].Data,
                               haffmanAC[0].Flags,
                               haffmanAC[0].Data);

      MessageBox.Show(jpegdata);
   }
   catch (Exception ex)
   {
      MessageBox.Show(ex.Message);
   }

   session.Shutdown();
}

public void SetJpegCompressionExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

   try
   {
      TwainJpegCompression myJpeg = session.GetJpegCompression(false);
      TwainMemoryData[] data = new TwainMemoryData[1];

      myJpeg.ColorScheme = TwainColorScheme.Gray;
      myJpeg.SubSampling = 0x10001000;
      myJpeg.ColorComponentCount = 1;
      myJpeg.RestartFrequency = 0;

      int[] quantMap = new int[1];
      quantMap[0] = 0;
      myJpeg.SetQuantizationMap(quantMap);

      int[] haffmanMap = new int[1];
      haffmanMap[0] = 0;
      myJpeg.SetHuffmanMap(haffmanMap);
      myJpeg.SetQuantizationTable(data);
      myJpeg.SetHuffmanDC(data);
      myJpeg.SetHuffmanAC(data);

      session.SetJpegCompression(myJpeg, false);
      MessageBox.Show("Set JPEG compression options is success");
   }
   catch (Exception ex)
   {
      MessageBox.Show(ex.Message);
   }

   session.Shutdown();
}

