}


public void TwainExceptionExample(IWin32Window parent)
{
   TwainSession session = null;

   try
   {
      // initialize a new TWAIN session
      session = new TwainSession();
      session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);
      session.AcquirePage += new EventHandler<TwainAcquirePageEventArgs>(_twainSession_AcquirePage);

      // acquire a page, if paper jam, allow the user to retry
      bool done = false;
      while(!done)
      {
         try
         {
            DialogResult res = session.Acquire(TwainUserInterfaceFlags.Modal | TwainUserInterfaceFlags.Show);
            MessageBox.Show("Success");
            done = true;
         }
         catch(TwainException ex)
         {
            if(ex.Code == TwainExceptionCode.PaperJam)
            {
               if(MessageBox.Show("Paper jam.  Fix and retry?", "TWAIN", MessageBoxButtons.YesNo) == DialogResult.No)
                  done = true;
            }
            else
            {
               // other error, propogate
               throw ex;
            }
         }
      }
   }
   catch(TwainException ex)
   {
      MessageBox.Show(string.Format("Twain error:{0}Code: {1}{0}Message: {2}", Environment.NewLine, ex.Code, ex.Message));
   }
   catch(Exception ex)
   {
      MessageBox.Show(string.Format("Other error: Message:{0}", ex.Message));
   }
   finally
   {
      if(session != null)
         session.Shutdown();
   }
}

public void SetCodeMessageExample()
{
   // Get the initial FileFormat message
   TwainExceptionCode code = TwainExceptionCode.StopScan;
   string message = TwainException.GetCodeMessage(code);
   MessageBox.Show(string.Format("Code: {0}, message: {1}", code, message));

   // make sure that this message is used now
   try
   {
      throw new TwainException(code);
   }
   catch(Exception ex)
   {
      MessageBox.Show(ex.Message);
   }

   // change this message to a custom one
   TwainException.SetCodeMessage(code, "My new message");

   // make sure that this new message is used now
   try
   {
      throw new TwainException(code);
   }
   catch(Exception ex)
   {
      MessageBox.Show(ex.Message);
   }
}

public void TwainEnumerationCapabilityExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);

   TwainCapability twCap = new TwainCapability();

   twCap.Information.ContainerType = TwainContainerType.Enumeration;
   twCap.Information.Type = TwainCapabilityType.ImageUnits;

   twCap.EnumerationCapability.Count = 2;
   twCap.EnumerationCapability.ItemType = TwainItemType.Uint16;

   TwainCapabilityValue item = TwainCapabilityValue.UnitInches;
   twCap.EnumerationCapability.SetValue(0, item);

   twCap.EnumerationCapability.SetValue(1, TwainCapabilityValue.UnitTwips);

   twCap.EnumerationCapability.CurrentIndex = 0;
   twCap.EnumerationCapability.DefaultIndex = 0;

   session.SetCapability(twCap, TwainSetCapabilityMode.Set);
   session.Shutdown();
}

public void TwainEnumerationCapabilityGetValueExample(IWin32Window parent)
{
   TwainSession session = new TwainSession();
   session.Startup(parent, "manufacturer", "productFamily", "version", "application", TwainStartupFlags.None);


   TwainCapability twCap = session.GetCapability(TwainCapabilityType.ImageUnits, TwainGetCapabilityMode.GetValues);

   if (twCap.Information.ContainerType == TwainContainerType.Enumeration)
   {
      TwainEnumerationCapability enumCap = twCap.EnumerationCapability;

      for (int i = 0; i < enumCap.Count ; i++)
      {
         TwainCapabilityValue capVal = (TwainCapabilityValue)enumCap.GetValue(i);
         string msg = string.Format("Capability Value(%d) = %s", i, capVal.ToString());
         MessageBox.Show(msg);
      }
   }
   else
      MessageBox.Show("Container type is not enumeration");
}

